import '../wallet.scss';
import React, { useState, useEffect, useRef } from 'react';
import { Table } from 'reactstrap';
import Axios from 'axios';
import { connect } from 'react-redux';
import { IRootState } from 'app/shared/reducers';
import { RouteComponentProps } from 'react-router-dom';
import moment from 'moment';
import { setWalletData } from 'app/shared/reducers/reports';
import ReactToPrint from 'react-to-print';
import { isEmpty } from 'lodash';
import exportFromJSON from 'export-from-json';
import MoneyFormat from 'react-number-format';
import processData, { xcelData } from './ProcessDataToExport';

export interface IStatementProps extends StateProps, DispatchProps, RouteComponentProps<{}> {}
function Wallet(props: IStatementProps) {
  const [statement, setStatement] = useState([
    { transactionRef: '', currentBalance: '', credit: false, debit: false, memo: '', transactionDate: '', externalRef: '' },
  ]);
  const [opening, setOpening] = useState('');
  const [closing, setClosing] = useState('');
  const [data, setData] = useState({
    accountOwnerName: '',
    dateOpened: moment(Date()).format('YYYY-MM-DD'),
    accountNumber: '',
    schemeName: '',
    start: '',
    end: '',
    scheme: '',
  });
  const [format, setFormat] = useState('pdf');
  const toPrint = useRef();

  const exportFile = async () => {
    enum toexport {
      csv = 'csv',
      xls = 'csv',
      txt = 'txt',
      json = 'json',
    }
    let dataToExport: xcelData[];
    try {
      dataToExport = await processData(statement);
      console.error(dataToExport);
    } catch (error) {
      console.error(error);
    }

    const fileName = 'specswallet Report';
    exportFromJSON({ data: dataToExport, fileName, exportType: toexport[format] });
  };

  const getStatement = async info => {
    setData({ ...data, ...info });
    try {
      const getWalletAccount = await Axios.get(
        `/api/account-statement/scheme/${!isEmpty(info.start) ? info.start : info.dateOpened}/${
          !isEmpty(info.end) ? info.end : moment(Date()).format('YYYY-MM-DD')
        }/${info.schemeID}`
      );
      // if(getWalletAccount.data.data.length && getWalletAccount.data.data.length > 0) {
      //     setOpening(getWalletAccount.data.data[0]['currentBalance']);
      //     setClosing(getWalletAccount.data.data[getWalletAccount.data.length - 1]['currentBalance']);
      // }
      setStatement(getWalletAccount.data.data);
      console.error(getWalletAccount.data);
    } catch (e) {
      console.error(e);
    }
  };
  useEffect(() => {
    getStatement(props.data);
    //  console.error(props.data,  'hello');
  }, [props.data]);

  return (
    <>
      <div style={{ float: 'right' }}>
        <select onChange={e => setFormat(e.target.value)} style={{ height: '50px', marginRight: '10px' }} className="formatprint">
          <option value="pdf">pdf</option>
          <option value="xls">xls</option>
          <option value="csv">csv</option>
          <option value="txt">text</option>
          <option value="json">json</option>
        </select>
        {format === 'pdf' && (
          <ReactToPrint
            trigger={() => {
              return (
                <button style={{ marginTop: '10px', marginRight: '40px', border: '1px solid #565682', fontSize: '12px' }}>
                  Download or print
                </button>
              );
            }}
            content={() => toPrint.current}
          />
        )}
        {format !== 'pdf' && (
          <button onClick={exportFile} style={{ marginTop: '10px', marginRight: '40px', border: '1px solid #565682', fontSize: '12px' }}>
            Print {format}
          </button>
        )}
      </div>
      <section ref={toPrint}>
        <span className="titles"> Global Wallet Statement</span>
        <div style={{ marginLeft: '54px' }}>
          <p className="titles2">Summary</p>
          <div className="dateContainer">
            <table>
              <thead></thead>
              <tbody>
                {/* <tr className = 'drow'>
                               <th>Wallet Owner Name: </th>
                               <td>{data.accountOwnerName}</td>
                           </tr> */}
                {/* <tr className = 'drow'>
                               <th>Data Created: </th>
                               <td>{data.dateOpened}</td>
                           </tr> */}
                <tr className="drow">
                  <th>Date Range: </th>
                  <td>
                    <b>{!isEmpty(data.start) ? moment(data.start).format('ll') : moment(data.dateOpened).format('ll')}</b> -{' '}
                    <b>{!isEmpty(data.end) ? moment(data.end).format('ll') : moment(Date()).format('ll')}</b>
                  </td>
                </tr>
                {!isEmpty(data.scheme) && (
                  <tr className="drow">
                    <th>Scheme Name: </th>
                    <td> {data.scheme}</td>
                  </tr>
                )}
                {/* <tr className = 'drow'>
                               <th>Opening Balance: </th>
                               <td>₦{opening}</td>
                           </tr>
                           <tr className = 'drow'>
                               <th>Closing Balance: </th>
                               <td>₦{closing}</td>
                           </tr> */}
              </tbody>
            </table>
          </div>
        </div>
        <div style={{ marginLeft: '54px', marginTop: '50px' }}>
          <div>
            <p className="titles2">REPORT DETAILS</p>
          </div>
          <Table hover className="transaction-table">
            <thead>
              <th>S/N</th>
              <th>Transaction Date</th>
              <th>Reference Number</th>
              <th>Narration</th>
              <th>External Reference</th>
              <th>Debit (₦)</th>
              <th>Credit (₦)</th>
              <th>Balance (₦)</th>
            </thead>
            <tbody>
              {!isEmpty(statement) &&
                statement.map((v, i) => (
                  <tr className="tdata" key={i}>
                    <td>{i + 1}</td>
                    <td>{moment(v.transactionDate).format('ll')}</td>
                    <td>{v.transactionRef}</td>
                    <td>{v.memo}</td>
                    <td>{v.externalRef !== 'null' ? v.externalRef : ''}</td>
                    <td>
                      <MoneyFormat
                        value={Number(v.debit)}
                        displayType="text"
                        thousandSeparator={true}
                        decimalScale={2}
                        fixedDecimalScale={true}
                        // prefix = "₦"
                      />
                    </td>
                    <td>
                      <MoneyFormat
                        value={Number(v.credit)}
                        displayType="text"
                        thousandSeparator={true}
                        decimalScale={2}
                        fixedDecimalScale={true}
                        // prefix = "₦"
                      />
                    </td>
                    <td>
                      <MoneyFormat
                        value={Number(v.currentBalance)}
                        displayType="text"
                        thousandSeparator={true}
                        decimalScale={2}
                        fixedDecimalScale={true}
                        // prefix = "₦"
                      />
                    </td>
                  </tr>
                ))}
            </tbody>
          </Table>
        </div>
      </section>
    </>
  );
}

const mapStateToProps = ({ reports }: IRootState) => ({
  data: reports.data,
});

const mapDispatchToProps = { setWalletData };

type StateProps = ReturnType<typeof mapStateToProps>;
type DispatchProps = typeof mapDispatchToProps;

export default connect(mapStateToProps, mapDispatchToProps)(Wallet);
