import React, { useState, useEffect } from 'react';
import './layout.scss';
import { SidebarNav } from './components/SidebarNavs';
import { Link, NavLink, RouteComponentProps } from 'react-router-dom';
import { connect } from 'react-redux';
import { hasAnyAuthority } from 'app/shared/util/hasAuthorities';
import { AUTHORITIES } from 'app/config/constants';
import Header from 'app/modules/dashboard/header/Header'; // './Header';
import { IoMdArrowDropdown, IoMdArrowDropup } from 'react-icons/io';
// export interface ILayouts extends StateProps, React.ReactElement {}
const width = 270;
const Layout: any = props => {
  const [activeLink, setActiveLink] = useState(0);
  const [xPosition, setX] = React.useState(-width);
  const [size, setSize] = useState('');
  const [active1, setActive1] = useState(false);

  const toggleActive = () => setActive1(!active1);

  const toggleMenu = () => {
    if (window.innerWidth > 995) return;
    if (xPosition < 0) {
      setX(0);
    } else {
      setX(-width);
    }
  };

  React.useEffect(() => {
    window.innerWidth > 995 ? setX(0) : setX(-width);
  }, []);

  return (
    <>
      <Header toggleMenu={toggleMenu} isAuthenticated={props.isAuthenticated} />
      <section className="layoutContainer">
        <div
          style={{
            transform: `translatex(${xPosition}px)`,
            width,
          }}
          className="side-bar"
        >
          <div className="side-content" style={{ position: 'fixed', height: '80vh', overflow: 'scroll' }}>
            <div>
              {hasAnyAuthority(props.account.authorities, [AUTHORITIES.ADMIN, AUTHORITIES.ROLE_SUPER_ADMIN]) && (
                <NavLink
                  isActive={(match, location) => {
                    if (match) return false;
                    setActiveLink(0);
                    return true;
                  }}
                  activeClassName="sideNavActive"
                  to="/dash"
                >
                  <SidebarNav active={activeLink === 0} nav="Overview" />
                </NavLink>
              )}
              <NavLink
                isActive={(match, location) => {
                  if (!match) return false;
                  setActiveLink(1);
                  return true;
                }}
                activeClassName="sideNavActive"
                to="/customers"
              >
                <SidebarNav active={activeLink === 1} nav="Customers" />
              </NavLink>
              <NavLink
                isActive={(match, location) => {
                  if (!match) return false;
                  setActiveLink(2);
                  return true;
                }}
                activeClassName="sideNavActive"
                to="/dwallets"
              >
                <SidebarNav active={activeLink === 2} nav="Wallets" />
              </NavLink>
              <NavLink
                isActive={(match, location) => {
                  if (!match) return false;
                  setActiveLink(3);
                  return true;
                }}
                activeClassName="sideNavActive"
                to="/alltransactions"
              >
                <SidebarNav active={activeLink === 3} nav="Transactions" />
              </NavLink>
              <NavLink
                isActive={(match, location) => {
                  if (!match) return false;
                  setActiveLink(4);
                  return true;
                }}
                activeClassName="sideNavActive"
                to="/reports"
              >
                <SidebarNav active={activeLink === 4} nav="Reports" />
              </NavLink>
              <NavLink
                isActive={(match, location) => {
                  if (!match) return false;
                  setActiveLink(20);
                  return true;
                }}
                activeClassName="sideNavActive"
                to="/cashconnect-reports"
              >
                <SidebarNav active={activeLink === 20} nav="Reports (CashConnect)" />
              </NavLink>
              <NavLink
                isActive={(match, location) => {
                  if (!match) return false;
                  setActiveLink(40);
                  return true;
                }}
                activeClassName="sideNavActive"
                to="/end-reports"
              >
                <SidebarNav active={activeLink === 40} nav="End of Day Report" />
              </NavLink>
              <NavLink
                isActive={(match, location) => {
                  if (!match) return false;
                  setActiveLink(5);
                  return true;
                }}
                activeClassName="sideNavActive"
                to="/health"
              >
                <SidebarNav active={activeLink === 5} nav="Service Uptime" />
              </NavLink>
              <NavLink
                isActive={(match, location) => {
                  if (!match) return false;
                  setActiveLink(6);
                  return true;
                }}
                activeClassName="sideNavActive"
                to="/verifyRRR"
              >
                <SidebarNav active={activeLink === 6} nav="Verify RRR" />
              </NavLink>
              {/* <NavLink isActive = {(match, location) => {
                        if(!match) return false
                        setActiveLink(7); return true
                   }} activeClassName = 'sideNavActive' to = '/rights'>
                       <SidebarNav active = {activeLink === 7} nav = 'Access Rights'/>
                   </NavLink> */}
              <span onClick={toggleActive} className="inactive_nav">
                <table>
                  <tbody>
                    <tr>
                      <td>
                        <SidebarNav nav="Administration" />{' '}
                      </td>
                      <td>
                        {!active1 ? (
                          <IoMdArrowDropdown style={{ marginLeft: '10px' }} />
                        ) : (
                          <IoMdArrowDropup style={{ marginLeft: '10px' }} />
                        )}
                      </td>
                    </tr>
                  </tbody>
                </table>
              </span>
              {active1 && (
                <div style={{ background: '#F2F5FB', marginLeft: '20px' }}>
                  <NavLink
                    isActive={(match, location) => {
                      if (!match) return false;
                      setActiveLink(10);
                      return true;
                    }}
                    activeClassName="sideNavActive"
                    to="/create-request"
                  >
                    <SidebarNav active={activeLink === 10} nav="Create Request" />
                  </NavLink>
                  <NavLink
                    isActive={(match, location) => {
                      if (!match) return false;
                      setActiveLink(12);
                      return true;
                    }}
                    activeClassName="sideNavActive"
                    to="/request-status"
                  >
                    <SidebarNav active={activeLink === 12} nav="Request Status" />
                  </NavLink>
                  <NavLink
                    isActive={(match, location) => {
                      if (!match) return false;
                      setActiveLink(11);
                      return true;
                    }}
                    activeClassName="sideNavActive"
                    to="/requests"
                  >
                    <SidebarNav active={activeLink === 11} nav="Approve Requests" />
                  </NavLink>
                  {hasAnyAuthority(props.account.authorities, [AUTHORITIES.ADMIN, AUTHORITIES.OPERATIONS]) && (
                    <NavLink
                      isActive={(match, location) => {
                        if (!match) return false;
                        setActiveLink(8);
                        return true;
                      }}
                      activeClassName="sideNavActive"
                      to="/schemes"
                    >
                      <SidebarNav active={activeLink === 8} nav="Scheme Management" />
                    </NavLink>
                  )}
                  <NavLink
                    isActive={(match, location) => {
                      if (!match) return false;
                      setActiveLink(9);
                      return true;
                    }}
                    activeClassName="sideNavActive"
                    to="/transit"
                  >
                    <SidebarNav active={activeLink === 9} nav="Transit statement" />
                  </NavLink>
                  <NavLink
                    isActive={(match, location) => {
                      if (!match) return false;
                      setActiveLink(90);
                      return true;
                    }}
                    activeClassName="sideNavActive"
                    to="/app-management"
                  >
                    <SidebarNav active={activeLink === 90} nav="App Management" />
                  </NavLink>
                  <NavLink
                    isActive={(match, location) => {
                      if (!match) return false;
                      setActiveLink(24);
                      return true;
                    }}
                    activeClassName="sideNavActive"
                    to="/super-agents"
                  >
                    <SidebarNav active={activeLink === 24} nav="Super Agents" />
                  </NavLink>
                  <NavLink
                    isActive={(match, location) => {
                      if (!match) return false;
                      setActiveLink(14);
                      return true;
                    }}
                    activeClassName="sideNavActive"
                    to="/approval-groups"
                  >
                    <SidebarNav active={activeLink === 14} nav="Approval Groups" />
                  </NavLink>
                  <NavLink
                    isActive={(match, location) => {
                      if (!match) return false;
                      setActiveLink(13);
                      return true;
                    }}
                    activeClassName="sideNavActive"
                    to="/approval-workflow"
                  >
                    <SidebarNav active={activeLink === 13} nav="Approval Workflow" />
                  </NavLink>
                  <NavLink
                    isActive={(match, location) => {
                      if (!match) return false;
                      setActiveLink(17);
                      return true;
                    }}
                    activeClassName="sideNavActive"
                    to="/show-nuban"
                  >
                    <SidebarNav active={activeLink === 17} nav="Show Nuban" />
                  </NavLink>
                  <NavLink
                    isActive={(match, location) => {
                      if (!match) return false;
                      setActiveLink(18);
                      return true;
                    }}
                    activeClassName="sideNavActive"
                    to="/kyc-request"
                  >
                    <SidebarNav active={activeLink === 18} nav="KYC Request" />
                  </NavLink>
                  <NavLink
                    isActive={(match, location) => {
                      if (!match) return false;
                      setActiveLink(19);
                      return true;
                    }}
                    activeClassName="sideNavActive"
                    to="/kyc-docs"
                  >
                    <SidebarNav active={activeLink === 19} nav="KYC Documents" />
                  </NavLink>
                </div>
              )}
            </div>

            {/* <SidebarNav nav = 'Analytics'/> */}
            {/* <SidebarNav nav = 'Settings'/> */}
          </div>
        </div>
        <div
          style={{
            transform: `translatex(${xPosition + width}px)`,
            overflow: 'hidden',
            marginRight: window.innerWidth > 995 ? width : 0,
            marginLeft: window.innerWidth > 995 ? '30px' : 0,
            transition: '0.8s ease',
            marginTop: '83px',
          }}
        >
          {props.children}
          {window.innerWidth < 995 && xPosition === 0 && <div onClick={toggleMenu} className="blindFold"></div>}
        </div>
      </section>
    </>
  );
};
const mapStateToProps = storeState => ({
  account: storeState.authentication.account,
  isAuthenticated: storeState.authentication.isAuthenticated,
});
type StateProps = ReturnType<typeof mapStateToProps>;
export default connect(mapStateToProps)(Layout);
