import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import moment from 'moment';
import {Table} from 'reactstrap';
import MoneyFormat from 'react-number-format';

export default function (props) {
   const [state, setState] = useState({
        journal: {
            transDate: '',
            memo: ''
        },
        journalLines: []
    })

   const getTransaction = async () => {
        try {
            const journal = await Axios.get(`/api/daybook/${props.reference}`);
            console.error(journal);
            setState(journal.data)

        }catch(e) {
            console.error(e);
        }
    }

    useEffect(() => {
        getTransaction();
    }, []);
    
       let totalCredit = 0;
       let totalDebit = 0;
        return(
            <>
                    <div>
                    <p style = {{
                        padding: "10px",
                        backgroundColor: '#DB5328',
                        color: '#fff',
                        fontWeight: 'bold'
                    }}> {moment(state.journal.transDate).format('LLL')} {state.journal.memo}</p>
                    <Table>
                        <thead>
                            <th>Account Name</th>
                            <th>Credit</th>
                            <th>Debit</th>
                        </thead>
                        <tbody>
                            {state.journalLines.map((jl, k)=>{
                                totalCredit+=jl.credit;
                                totalDebit+=jl.debit
                                return(
                                    <tr key = {k}>
                                    <td style = {{width:"50%"}}> {jl.accountName} </td>
                                    <td>
                                        <MoneyFormat
                                        value = {jl.credit}
                                        displayType = 'text'
                                        thousandSeparator = {true}
                                        prefix = "₦"
                                        />
                                    </td>
                                    <td>
                                        <MoneyFormat
                                        value = {jl.debit}
                                        displayType = 'text'
                                        thousandSeparator = {true}
                                        prefix = "₦"
                                        />
                                    </td>
                                    </tr>
                                )
                            })}
                            <tr>
                                <th>Total</th>
                                <th>
                                    <MoneyFormat
                                    value = {totalCredit}
                                    displayType = 'text'
                                    thousandSeparator = {true}
                                    prefix = "₦"
                                    />
                                </th>
                                <th>
                                    <MoneyFormat
                                    value = {totalDebit}
                                    displayType = 'text'
                                    thousandSeparator = {true}
                                    prefix = "₦"
                                    />
                                </th>
                            </tr>
                        </tbody>
                    </Table>
                </div>
            </>
        )
   
}
